mapper.select:
   select count(id) from  equipment where 1 = 1

			 and CONCAT(imei)  LIKE concat(concat('%',#{keyWord}),'%')


			 and  eq_status  = #{eqStatus}


			 and  updatetime  like concat(concat('%',#{time}),'%')


			 and  agentid = #{agentid}

		SELECT * FROM  equipment  where 1 = 1 

			 and CONCAT(imei)  LIKE concat(concat('%',#{keyWord}),'%')


			 and  eq_status  = #{eqStatus}


			 and  updatetime  like concat(concat('%',#{time}),'%')


			 and  agentid  = #{agentid}

limit #{pageNo},#{pageSize}
