mapper.insert:
insert into jfhealth_new (id, HRV, sbp_ave, 
  dbp_ave, Heartrate, Bloodoxygen, 
  microcirculation, respirationrate, phone, 
  imei, createtime, Amedicalreport
  )
values (#{id,jdbcType=INTEGER}, #{HRV,jdbcType=INTEGER}, #{sbpAve,jdbcType=INTEGER}, 
  #{dbpAve,jdbcType=INTEGER}, #{heartrate,jdbcType=INTEGER}, #{bloodoxygen,jdbcType=INTEGER}, 
  #{microcirculation,jdbcType=INTEGER}, #{respirationrate,jdbcType=INTEGER}, #{phone,jdbcType=VARCHAR}, 
  #{imei,jdbcType=VARCHAR}, #{createtime,jdbcType=TIMESTAMP}, #{amedicalreport,jdbcType=LONGVARCHAR}
  )

select.if:
SELECT TRUNCATE(avg(sleeping),0) as sleeping,TRUNCATE(avg(step_when),0) as step_when,
date_format(createtime,'%Y-%m-%d %H:%i:%S') as date FROM equipment_data WHERE
DATE_FORMAT(createtime,'%Y-%m-%d')=#{timedata} AND user_id=#{userId}
GROUP BY date_format(createtime, '%Y%m%d %H:%i:%S');

SELECT TRUNCATE(avg(sleeping),0) as sleeping,TRUNCATE(avg(step_when),0) as step_when,
date_format(createtime, '%Y-%m-%d')
as date FROM equipment_data WHERE
YEARWEEK(DATE_FORMAT(createtime,'%Y-%m-%d'))=YEARWEEK(NOW()) AND
user_id=#{userId}
GROUP BY date_format(createtime, '%Y%m%d');

SELECT TRUNCATE(avg(HRV),0) as HRV,TRUNCATE(avg(sbp_ave),0) as sbp_ave,TRUNCATE(avg(dbp_ave),0) as dbp_ave,TRUNCATE(avg(Heartrate),0) as Heartrate,
TRUNCATE(avg(Bloodoxygen),0) as Bloodoxygen,TRUNCATE(avg(microcirculation),0) as microcirculation,TRUNCATE(avg(respirationrate),0) as respirationrate,
date_format(createtime,'%Y-%m-%d %H:%i:%S') as date FROM jfhealth_new WHERE
DATE_FORMAT(createtime,'%Y-%m-%d')=#{timedata} AND phone=#{phone}
GROUP BY date_format(createtime, '%Y%m%d %H:%i:%S');

mapper.select:
SELECT * FROM jfhealth_new
where CONCAT(imei) LIKE concat(concat('%',#{keyWord}),'%')
order by createtime desc limit #{pageNo},#{pageSize}
